describe('DELETE Request Session Cleanup', () => {
  it('should shutdown MCP server and clean up Redis channels on DELETE request', async () => {
    // First, create a session with an initialization request
    const initRequest: JSONRPCMessage = {
      jsonrpc: '2.0',
      id: 'init-1',
      method: 'initialize',
      params: {
        protocolVersion: '2024-11-05',
        capabilities: {},
        clientInfo: { name: 'test-client', version: '1.0.0' }
      }
    };

    mockReq.body = initRequest;
    mockReq.auth = {
      clientId: 'test-client-123',
      token: 'test-token',
      scopes: ['mcp'],
      extra: { userId: 'test-user-123' }
    } as AuthInfo;

    // Initialize session
    await handleStreamableHTTP(mockReq as Request, mockRes as Response);

    // Get the actual session ID from created channels
    const allChannels = Array.from(mockRedis.subscribers.keys());
    const serverChannel = allChannels.find(channel => channel.includes('mcp:shttp:toserver:'));
    const sessionId = serverChannel?.split(':')[3]; // Session ID is the 4th part

    expect(sessionId).toBeDefined();

    // Mock the session as live and owned by the user
    mockRedis.set(`session:${sessionId}`, 'live');
    mockRedis.set(`session:${sessionId}:owner`, 'test-user-123');

    // Reset mocks
    jest.clearAllMocks();

    // Now test DELETE request
    mockReq.method = 'DELETE';
    mockReq.headers = {
      ...mockReq.headers,
      'mcp-session-id': sessionId
    };
    mockReq.body = {};

    // Track control messages sent to Redis
    const controlMessages: any[] = [];
    const originalPublish = mockRedis.publish.bind(mockRedis);
    mockRedis.publish = jest.fn(async (channel: string, message: string) => {
      if (channel.includes('mcp:control:')) {
        controlMessages.push({ channel, message: JSON.parse(message) });
      }
      return originalPublish(channel, message);
    });

    await handleStreamableHTTP(mockReq as Request, mockRes as Response);

    // Check response
    expect(mockRes.status).toHaveBeenCalledWith(200);
    expect(mockRes.json).toHaveBeenCalledWith({
      jsonrpc: '2.0',
      result: { status: 'Session terminated successfully' },
      id: null,
    });

    // Check that control message was sent
    expect(controlMessages).toHaveLength(1);
    expect(controlMessages[0].channel).toBe(`mcp:control:${sessionId}`);
    expect(controlMessages[0].message).toMatchObject({
      type: 'control',
      action: 'SHUTDOWN'
    });
  });

  it('should return 404 for DELETE request with invalid session ID', async () => {
    const invalidSessionId = 'invalid-session-id';

    mockReq.method = 'DELETE';
    mockReq.headers = {
      ...mockReq.headers,
      'mcp-session-id': invalidSessionId
    };
    mockReq.body = {};
    mockReq.auth = {
      clientId: 'test-client-123',
      token: 'test-token',
      scopes: ['mcp'],
      extra: { userId: 'test-user-123' }
    } as AuthInfo;

    await handleStreamableHTTP(mockReq as Request, mockRes as Response);

    // Should return 404 for non-existent session or access denied
    expect(mockRes.status).toHaveBeenCalledWith(404);
    expect(mockRes.json).toHaveBeenCalledWith({
      jsonrpc: '2.0',
      error: {
        code: -32001,
        message: 'Session not found or access denied',
      },
      id: null,
    });
  });
});

