input.new_string:
afterEach(async () => {
  // Always trigger cleanup for any MCP servers created during tests
  await triggerResponseCleanup();

  // Send shutdown control messages to all active sessions
  const allChannels = Array.from(mockRedis.subscribers.keys());
  const sessionChannels = allChannels.filter(channel => channel.includes('mcp:shttp:toserver:'));
  for (const channel of sessionChannels) {
    const sessionId = channel.split(':')[3];
    if (sessionId) {
      // Send shutdown control message
      await mockRedis.publish(`mcp:control:${sessionId}`, JSON.stringify({
        type: 'control',
        action: 'SHUTDOWN',
        timestamp: Date.now()
      }));
    }
  }

  // Wait a bit for shutdowns to complete
  await new Promise(resolve => setTimeout(resolve, 50));

  mockRedis.clear();
  jest.clearAllMocks();
});

